import axios from "axios";
console.log(process.env.REACT_APP_API_KEY);
let searchterm = "London&client_id=";
const apikey = process.env.REACT_APP_API_KEY;
let query = 'https://api.unsplash.com/search/photos?query=' + searchterm + apikey;
console.log(query);


export default {
  
  getAuth: function(email,password) {
     return axios.get("/api/auth/" + email + "/" + password  );

  },
  
  saveAuth: function(authData) {
    console.log("authData", authData)
    return axios.post("/api/auth", authData);
  },

  findData:function(){
    return axios.get(query);
  }
};
